import React, { useEffect, useState } from 'react';
import { Table, Button, Form, Modal } from 'react-bootstrap';
import { getAllUser, updateUser, deleteUser, blockUser, createUser } from '../api/UserRequest';

const UserListAdmin = () => {
    const [users, setUsers] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({ firstname: '', lastname: '', username: '', country: '' });
    const [editingUserId, setEditingUserId] = useState(null);

    // Fetch all users on component mount
    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await getAllUser();
            console.log('Fetched Users:', response.data); // Debugging API response
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    // Handle Create or Edit User
    const handleSaveUser = async () => {
        try {
            if (editingUserId) {
                // Edit User
                const response = await updateUser(editingUserId, formData);
                setUsers((prevUsers) =>
                    prevUsers.map((user) => (user._id === editingUserId ? response.data.user : user))
                );
            } else {
                // Create User
                const response = await createUser(formData);
                setUsers((prevUsers) => [...prevUsers, response.data.user]);
            }
            setShowModal(false);
            setFormData({ firstname: '', lastname: '', username: '', country: '' });
            setEditingUserId(null);
        } catch (error) {
            console.error('Error saving user:', error);
        }
    };

    // Open Modal for Edit
    const handleEditUser = (user) => {
        console.log('Edit button clicked for:', user); // Debugging
        setFormData({ firstname: user.firstname, lastname: user.lastname, username: user.username, country: user.country });
        setEditingUserId(user._id);
        setShowModal(true);
    };

    // Handle Delete User
    const handleDeleteUser = async (id) => {
        try {
            await deleteUser(id);
            setUsers((prevUsers) => prevUsers.filter((user) => user._id !== id));
            alert('User deleted successfully');
        } catch (error) {
            console.error('Error deleting user:', error);
        }
    };

    // Handle Block User
    const handleBlockUser = async (id) => {
        try {
            await blockUser(id);
            alert('User blocked successfully');
        } catch (error) {
            console.error('Error blocking user:', error);
        }
    };

    // Open Modal for Create User
    const handleCreateUser = () => {
        setFormData({ firstname: '', lastname: '', username: '', country: '' });
        setEditingUserId(null);
        setShowModal(true);
    };

    const PF = process.env.REACT_APP_PUBLIC_FOLDER;

    return (
        <>
            <Button variant="primary" className="mb-3" onClick={handleCreateUser}>
                Create New User
            </Button>

            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Profile</th>
                        <th>Full Name</th>
                        <th>Username</th>
                        <th>Followers</th>
                        <th>Country</th>
                        <th>Created Date</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user._id}>
                            <td>
                                <img
                                    src={user.profilePicture ? `${PF}${user.profilePicture}` : `${PF}default.png`}
                                    alt="Profile"
                                    className="rounded-circle"
                                    width="40"
                                />
                            </td>
                            <td>{`${user.firstname} ${user.lastname}`}</td>
                            <td>{user.username}</td>
                            <td>{user.followers.length}</td>
                            <td>{user.country}</td>
                            <td>{new Date(user.createdAt).toLocaleString()}</td>
                            <td>
                                <Button
                                    variant="info"
                                    size="sm"
                                    className="me-2"
                                    onClick={() => handleEditUser(user)}
                                >
                                    Edit
                                </Button>
                                <Button
                                    variant="danger"
                                    size="sm"
                                    className="me-2"
                                    onClick={() => handleDeleteUser(user._id)}
                                >
                                    Delete
                                </Button>
                                <Button
                                    variant="warning"
                                    size="sm"
                                    onClick={() => handleBlockUser(user._id)}
                                >
                                    Block
                                </Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>

            {/* Create/Edit Modal */}
            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>{editingUserId ? 'Edit User' : 'Create User'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.firstname}
                                onChange={(e) => setFormData({ ...formData, firstname: e.target.value })}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.lastname}
                                onChange={(e) => setFormData({ ...formData, lastname: e.target.value })}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Username</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.username}
                                onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Country</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.country}
                                onChange={(e) => setFormData({ ...formData, country: e.target.value })}
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleSaveUser}>
                        Save
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
};

export default UserListAdmin;
