import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Logoa from "../../img/alogo.png";
import './Auth.css'
import { useDispatch, useSelector } from "react-redux";
import { logIn, signUp } from "../../components/actions/AuthAction";
import { useNavigate } from "react-router-dom";  // Import useNavigate

const Auth = () => {
  const dispatch = useDispatch();
  const loading = useSelector((state) => state.authReducer.loading)
  const [isSignUp, setIsSignUp] = useState(true);
  console.log(loading);

  const [data, setData] = useState({ firstname: "", lastname: "", username: "", password: "", confirmpass: "" })

  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value })
  }
  const [confirmpass, setConfirmPass] = useState(true);
  const navigate = useNavigate();  // Initialize navigate

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if username and password are "admin1" and navigate to admin page
    if (data.username === "admin1" && data.password === "admin1") {
      localStorage.setItem("userId", "6757eeeabacab91a5683007c");
      navigate("/admin");  // Navigate to the admin page
      return;  // Stop further execution
    }


    if (isSignUp) {
      data.password === data.confirmpass ? dispatch(signUp(data)) : setConfirmPass(false);

    } else {
      dispatch(logIn(data));
    }
  }
  const resetForm = () => {
    setConfirmPass(true);
    setData({
      firstname: "",
      lastname: "",
      username: "",
      password: "",
      confirmpass: ""
    })
  }

  return (
    // Left Side

    <div className="mainBody">
      <div className="d-flex flex-column flex-md-row align-items-center justify-content-center vh-100">
        <div className="a-left p-3 d-flex flex-column align-items-center text-center text-md-start mb-4 mb-md-0 me-md-5">
          <img src={ Logoa } alt="Logo" className="img-fluid" style={ { maxWidth: '8rem', maxHeight: '8rem' } } />
          <div className="Webname">
            <h1>
              <span style={ { color: '#2980b9' } }>Chat</span>
              <span style={ { color: '#239b56' } }>Link</span>
            </h1>
            <h6 className="text-warning animatedText">Where Conversations Meet Connections</h6><br/><br/>
          </div>
        </div>

        {/* Right Side */ }
        <div className="a-right">
          <form className="infoForm authForm p-4 rounded border" style={{backgroundColor:'red'}} onSubmit={ handleSubmit }>
            <h3 className="text-primary text-center">{ isSignUp ? "Sign up" : "Login" }</h3><br />


            { isSignUp &&
              <div className="row g-2 mb-3">
                <div className="col">
                  <input
                    type="text"
                    placeholder="First Name"
                    className="form-control"
                    name="firstname"
                    onChange={ handleChange }
                  />
                </div>
                <div className="col">
                  <input
                    type="text"
                    placeholder="Last Name"
                    className="form-control"
                    name="lastname"
                    onChange={ handleChange }
                  />
                </div>
              </div>
            }

            <div className="mb-3">
              <input
                type="text"
                className="form-control"
                name="username"
                placeholder="Username"
                onChange={ handleChange }
                value={ data.username }
              />
            </div>
            <div className="row g-2 mb-3">
              <div className="col">
                <input
                  type="password"
                  className="form-control"
                  name="password"
                  placeholder="Password"
                  onChange={ handleChange }
                  value={ data.password }
                />
              </div>
              { isSignUp &&
                <div className="col">
                  <input
                    type="password"
                    className="form-control"
                    name="confirmpass"
                    placeholder="Confirm Password"
                    onChange={ handleChange }
                    value={ data.confirmpass }
                  />
                </div>
              }

            </div>
            <div className="mb-3 text-center">
              <span className="text-danger" style={ { fontSize: '12px', display: confirmpass ? "none" : "block" } } >* Confirm Password is not same!</span>
              <span className="text-primary" style={ { fontSize: '16px', cursor: 'pointer' } }
                onClick={ () => { setIsSignUp((prev) => !prev); resetForm() } }>
                { isSignUp ? "Already have an account? Login!" : "Don't have an account? Sign up!" }</span>
            </div>
            <button className="button w-100" type="submit" disabled={ loading }>{ loading ? "Loading..." : isSignUp ? "Sign up" : "Login" }</button>
          </form>
        </div>

      </div>
    </div>
  );
};



export default Auth;
