import React, { useEffect, useState } from 'react';
import { Table, Button } from 'react-bootstrap';
import { getTimelinePosts } from '../api/PostRequest'; // Import the API function

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const userId = '6757eeeabacab91a5683266c'; // Replace with actual user ID

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await getTimelinePosts(userId);
        setPosts(response.data); // Set the posts data from API response
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, [userId]);

  return (
    <>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Image</th>
            <th>Title</th>
            <th>Author</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {posts.map(post => (
            <tr key={post._id}> {/* Use post._id as the unique key */}
              <td>
                <img
                  src={post.image || 'default-image.jpg'} // Fallback if no image is available
                  alt="Post"
                  className="rounded"
                  width="40"
                />
              </td>
              <td>{post.title}</td>
              <td>{post.author}</td>
              <td>{new Date(post.createdAt).toLocaleDateString()}</td> {/* Format the date */}
              <td>
                <Button variant="info" size="sm" className="me-2">
                  View Post
                </Button>
                <Button variant="warning" size="sm" className="me-2">
                  Edit
                </Button>
                <Button variant="danger" size="sm">
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </>
  );
};

export default PostList;
