import React from 'react'
import './Sidebar.css'
import logo from '../../img/alogo.png'
import post from "../../img/share.png";
import noti from "../../img/noti.png";
import logout from "../../img/logout1.png";
import user1 from "../../img/user1.png";
import dashboard from "../../img/dashboard1.png";
import { useNavigate, Link } from 'react-router-dom'; // Import useNavigate
const Sidebar = () => {
    const navigate = useNavigate();
    const handleLogout = () => {
        // Redirect to the authentication page on logout
        localStorage.clear();
        navigate('/auth');
    };

    return (
        <>
            <div className="sidebar text-white p-3" style={ { width: '17%' } }>
                <div className="text-center">
                    <img
                        src={ logo }
                        alt="ChatLink Logo"
                        style={ { width: '100px', height: '100px', borderRadius: '50%', marginBottom: '20px' } }
                    />
                </div>
                <div className="d-flex flex-column">
                    <Link to="/dashboard" className="text-white text-decoration-none mb-3 d-flex align-items-center">
                        <img src={ dashboard } className="imglogo me-2" alt="Dashboard Icon" />
                        Dashboard
                    </Link>
                    <Link to="/viewUser" className="text-white text-decoration-none mb-3 d-flex align-items-center">
                        <img src={ user1 } className="imglogo me-2" alt="User Icon" />
                        View Users
                    </Link>
                    <Link to="/viewPost" className="text-white text-decoration-none mb-3 d-flex align-items-center">
                        <img src={ post } className="imglogo me-2" alt="Post Icon" />
                        View Posts
                    </Link>
                    <Link to="/Notification" className="text-white text-decoration-none mb-3 d-flex align-items-center">
                        <img src={ noti } className="imglogo me-2" alt="Notification Icon" />
                        Notification
                    </Link>
                    <button
                        onClick={ handleLogout }
                        className="btn text-white d-flex align-items-center bg-transparent border-0"
                    >
                        <img src={ logout } className="imglogo me-2" alt="Logout Icon" />
                        Logout
                    </button>
                </div>
            </div>

        </>
    )
}

export default Sidebar
