  const [modalOpened, setModalOpened] = useState(false); // State to manage modal

  return (
    <>
      <div className="main">
        <div className="blur" style={{ top: '-18%', right: '0' }}></div>
        <div className="blur" style={{ top: '36%', left: '-8rem' }}></div>

        <button onClick={() => setModalOpened(true)}>Open Share Modal</button> {/* Button to open modal */}

        <Home />
        {/* <Profile/> */}
        {/* <Auth/> */}
      </div>
      <ShareModal modalOpened={modalOpened} setModalOpened={setModalOpened} /> {/* Pass modal state */}
    </>
  );
}

export default App;
