import './App.css';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useSelector } from "react-redux";
import Blur from './components/blur/Blur';
import Auth from './pages/auth/Auth';
import Home from './pages/home/Home';
import Admin from './pages/admin/Admin'; // Import the Admin page

function App() {
  const user = useSelector((state) => state.authReducer.authData);

  return (
    <>
      <div className="main">
        <Blur />

        <Routes>
          {/* Default route: Redirect based on user's authentication and admin status */}
          <Route
            path="/"
            element={
              user ? (
                user.isAdmin ? (
                  <Navigate to="admin" />
                ) : (
                  <Navigate to="home" />
                )
              ) : (
                <Navigate to="auth" />
              )
            }
          />

          {/* Home route: Only accessible to authenticated non-admin users */}
          <Route
            path="/home"
            element={
              user ? (
                user.isAdmin ? (
                  <Navigate to="../admin" />
                ) : (
                  <Home />
                )
              ) : (
                <Navigate to="../auth" />
              )
            }
          />

          {/* Admin route: Only accessible to admin users */}
          <Route
            path="/admin"
            element={
              user && user.isAdmin ? <Admin /> : <Navigate to="../auth" />
            }
          />

          {/* Auth route: Redirects authenticated users based on their admin status */}
          <Route
            path="/auth"
            element={
              user ? (
                user.isAdmin ? (
                  <Navigate to="../admin" />
                ) : (
                  <Navigate to="../home" />
                )
              ) : (
                <Auth />
              )
            }
          />
        </Routes>
      </div>
    </>
  );
}

export default App;


