import React, { useState, useEffect } from 'react';
import './TrendCard.css';

import { getAllUser } from '../api/UserRequest';
import { getTimelinePosts } from '../api/PostRequest';

const TrendCard = () => {
  const [trends, setTrends] = useState([]);

  useEffect(() => {
    const fetchTrends = async () => {
      try {
        // Fetch all users
        const userResponse = await getAllUser();
        console.log('Users:', userResponse.data); // Log user data
        const userList = userResponse.data;

        // Fetch posts for all users
        const postPromises = userList.map((user) => getTimelinePosts(user._id));
        const postResponses = await Promise.all(postPromises);

        // Combine and process posts
        const posts = postResponses.flatMap((response) => response.data);
        console.log('Posts:', posts); // Log posts data

        // Extract hashtags from post descriptions
        const hashtagCounts = {};
        posts.forEach((post) => {
          const hashtags = post.desc?.match(/#[\w]+/g) || [];
          hashtags.forEach((hashtag) => {
            hashtagCounts[hashtag] = (hashtagCounts[hashtag] || 0) + 1;
          });
        });

        // Convert to array and sort by frequency
        const sortedTrends = Object.entries(hashtagCounts)
          .map(([name, count]) => ({ name, shares: count }))
          .sort((a, b) => b.shares - a.shares);

          console.log('Trends:', sortedTrends); // Log trends
        setTrends(sortedTrends);
      } catch (error) {
        console.error('Error fetching trends:', error);
      }
    };

    fetchTrends();
  }, []);

  return (
    <div className="TrendCard">
      <h3>Trends for you</h3>
      {trends.map((trend, index) => (
        <div className="trend" key={index}>
          <span>{trend.name}</span>
          <span>{trend.shares} shares</span>
        </div>
      ))}
    </div>
  );
};

export default TrendCard;
