import React, { useEffect, useState } from 'react'
import { Card, Row, Col } from 'react-bootstrap';
import { getAllUser} from '../api/UserRequest';
const Status = () => {
  const [users, setUsers] = useState([]);
  const [totalUsers, setTotalUsers] = useState(0);
  const [newUsersToday, setNewUsersToday] = useState(0);
  
  // Fetch all users on component mount
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await getAllUser();
        const usersData = response.data;
        setUsers(usersData);
        setTotalUsers(usersData.length); // Set the total number of users
  
        // Get today's date
        const today = new Date().setHours(0, 0, 0, 0); // Start of today (midnight)
  
        // Filter users created today
        const newUsers = usersData.filter(user => {
          const userCreatedAt = new Date(user.createdAt).setHours(0, 0, 0, 0);
          return userCreatedAt === today; // Check if the user's creation date is today
        });
  
        setNewUsersToday(newUsers.length); // Set the count of new users today
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };
    fetchUsers();
  }, []);
  return (
    <>
      <Row className="mb-4">
      <Col md={3}>
        <Card className="text-center bg-primary text-light">
          <Card.Body>
            <Card.Title>Total Users</Card.Title>
            <Card.Text>{totalUsers}</Card.Text>
          </Card.Body>
        </Card>
      </Col>
      <Col md={3}>
        <Card className="text-center bg-success text-light">
          <Card.Body>
            <Card.Title>Total Posts</Card.Title>
            <Card.Text>5678</Card.Text>
          </Card.Body>
        </Card>
      </Col>
      <Col md={3}>
        <Card className="text-center bg-warning text-light">
          <Card.Body>
            <Card.Title>Total Admins</Card.Title>
            <Card.Text>3</Card.Text>
          </Card.Body>
        </Card>
      </Col>
      <Col md={3}>
        <Card className="text-center bg-danger text-light">
          <Card.Body>
            <Card.Title>New Users Today</Card.Title>
            <Card.Text>{newUsersToday}</Card.Text>
          </Card.Body>
        </Card>
      </Col>
    </Row>
    </>
  )
}

export default Status
