import React from 'react';
import PostShare from "../postShare/PostShare";

function ShareModal({ modalOpened, setModalOpened }) {
  return (
    <div 
      className={`modal fade ${modalOpened ? 'show' : ''}`} 
      style={{ display: modalOpened ? 'block' : 'none' }} 
      tabIndex="-1" 
      aria-labelledby="shareModalLabel" 
      aria-hidden={!modalOpened}
    >
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="shareModalLabel">Share a Post</h5>
            <button 
              type="button" 
              className="close" 
              onClick={() => setModalOpened(false)} 
              aria-label="Close"
            >
              <span>&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <PostShare />
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-warning" onClick={() => setModalOpened(false)}>
              Close
            </button>
            <button type="button" className="btn btn-outline-primary">Share</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ShareModal;
