import React, { useState } from 'react';
import Sidebar from './Sidebar';
import Status from './Status';
import './AdminDashboard.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import PostList from './PostList';
import Notification from './Notification';
import UserListAdmin from './UserListAdmin';

//Admin User Id: 6757eeeabacab91a5683007c
const AdminDashboard = () => {
  const [showUserList, setShowUserList] = useState(false);
  const [showPostList, setShowPostList] = useState(false);

  // Function to toggle the visibility of the UserList
  const handleViewUsersClick = () => {
    setShowUserList(!showUserList);
    setShowPostList(false); // Hide PostList when showing UserList
  };

  // Function to toggle the visibility of the PostList
  const handleViewPostsClick = () => {
    setShowPostList(!showPostList);
    setShowUserList(false); // Hide UserList when showing PostList
  };

  return (
    <>
      <div className="container-fluid">
        <h1 className="text-center ad">Admin Dashboard</h1>
        <div className="row">
          {/* Sidebar */}
          <div className="col-3 col-md-2 bg-dark text-white vh-100 position-fixed">
            <Sidebar
              onViewUsersClick={handleViewUsersClick}
              onViewPostsClick={handleViewPostsClick}
            />
          </div>

          {/* Main content area with offset */}
          <div className="col-9 col-md-10 offset-md-2 ms-auto p-4">
            <Status />
            {/* {showUserList && <Notification />} */}
            {/* {showUserList && <UserList />} */}
            {showUserList && <UserListAdmin />}
            {showPostList && <PostList />}
          </div>
        </div>
      </div>
    </>
  );
};

export default AdminDashboard;
