const initialState = {
  authData: null, // Initially, authData is null
  loading: false,
  error: false,
  updateLoading: false,
};

const authReducer = (state = initialState, action) => {
  switch (action.type) {
    case "AUTH_START":
      return { ...state, loading: true, error: false };

    case "AUTH_SUCCESS":
      localStorage.setItem("profile", JSON.stringify({ ...action?.data }));
      return {
        ...state,
        authData: action.data, // Set authData to action.data (which should include user info)
        loading: false,
        error: false,
      };

    case "AUTH_FAIL":
      return { ...state, loading: false, error: true };

    case "LOG_OUT":
      localStorage.clear();
      return { ...state, authData: null, loading: false, error: false, updateLoading: false };

    default:
      return state;
  }
};

export default authReducer;
