import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { followUser, unFollowUser } from "../actions/userAction.js";
import { createChat } from "../actions/ChatAction.js";
const User = ({ person }) => {
  const publicFolder = process.env.REACT_APP_PUBLIC_FOLDER;
  const { user } = useSelector((state) => state.authReducer.authData);
  const dispatch = useDispatch()
  
  const [following, setFollowing] = useState(
    person.followers.includes(user._id)
  );

  const handleFollow = () => {
    if (!following) {
      // If the user is not following, dispatch the follow action
      dispatch(followUser(person._id, user));

      // After following, create a chat between the users
      const chatData = {
        senderId: user._id,
        receiverId: person._id,
      };
      dispatch(createChat(chatData)); // Call the createChat API
    } else {
      // If the user is already following, dispatch the unfollow action
      dispatch(unFollowUser(person._id, user));
    }

    setFollowing((prev) => !prev); // Toggle the following state
  };


  // const handleFollow = () => {
  //   following
  //     ? dispatch(unFollowUser(person._id, user))
  //     : dispatch(followUser(person._id, user));
  //   setFollowing((prev) => !prev);
  // };

  return (
    <div className="follower">
      <div>
        <img
          src={
            publicFolder + person.profilePicture
              ? publicFolder + person.profilePicture
              : publicFolder + "defaultProfile.png"
          }
          alt="profile"
          className="followerImage"
        />
        <div className="name">
          <span>{person.firstname}</span>
          <span>@{person.username}</span>
        </div>
      </div>
      <button
        className={
          following ? "button fc-button UnfollowButton" : "button fc-button"
        }
        onClick={handleFollow}
      >
        {following ? "Unfollow" : "Follow"}
      </button>
    </div>
  );
};

export default User;
