import React, { useEffect, useState } from 'react';
import { Table, Button } from 'react-bootstrap';
import { getAllUser, updateUser } from '../api/UserRequest'; // Import functions
import img1 from '../../img/img1.png';

const UserList = () => {
  const [users, setUsers] = useState([]);

  // Fetch all users on component mount
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await getAllUser();
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };
    fetchUsers();
  }, []);

  // Edit User
  const handleEditUser = async (id) => {
    const updatedData = { firstname: 'Updated', lastname: 'User' }; // Example data
    try {
      const response = await updateUser(id, updatedData);
      setUsers(users.map((user) => (user._id === id ? response.data.user : user)));
      alert('User updated successfully');
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Profile</th>
            <th>Full Name</th>
            <th>Username</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>
                <img src={img1} alt="Profile" className="rounded-circle" width="40" />
              </td>
              <td>{`${user.firstname} ${user.lastname}`}</td>
              <td>{user.username}</td>
              <td>
                <Button
                  variant="info"
                  size="sm"
                  className="me-2"
                  onClick={() => handleEditUser(user._id)}
                >
                  Edit
                </Button>
                <Button variant="danger" size="sm" className="me-2">
                  Delete
                </Button>
                <Button variant="warning" size="sm">
                  Block
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </>
  );
};

export default UserList;
