import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getNotifications, createNotification, markNotificationAsRead, deleteNotification, updateNotification } from "../actions/NotificationAction.js"; // Import your action creators
import { Modal, Button, Form } from "react-bootstrap"; // Import Bootstrap modal components

const Notification = () => {
  // Safely destructure user from authReducer
  const { user } = useSelector((state) => state.authReducer.authData || {});

  const dispatch = useDispatch();
  const { notifications, loading, error } = useSelector((state) => state.notifications);

  // State to manage modal visibility and notification data
  const [showModal, setShowModal] = useState(false);
  const [notificationData, setNotificationData] = useState({ message: "", id: null });

  // Check if user is available before proceeding
  if (!user) {
    return <div>Please log in to view notifications.</div>;
  }

  // Get user ID from user object (for regular users)
  const userId = user._id; // Assuming userId is stored in localStorage
  const adminUserId = '6757eeeabacab91a5683007c'; // Fixed admin user ID

  // Fetch notifications on component mount
  useEffect(() => {
    if (userId) {
      // Admin fetching notifications for all users
      if (userId === adminUserId) {
        dispatch(getNotifications(adminUserId));
      } else {
        // Regular user fetching their own notifications
        dispatch(getNotifications(userId));
      }
    }
  }, [dispatch, userId]); // Always run useEffect, but conditionally dispatch based on userId
  

  // Handler to create a new notification
  const handleCreateNotification = (newNotification) => {
    if (userId === adminUserId) {
      // Admin can create notifications for users
      dispatch(createNotification(adminUserId, newNotification));
    } else {
      // Regular user can create notifications for themselves
      dispatch(createNotification(userId, newNotification));
    }
    setShowModal(false); // Close modal after creating notification
  };

  // Handler to update an existing notification
  const handleUpdateNotification = (updatedNotification) => {
    dispatch(updateNotification(updatedNotification));
    setShowModal(false); // Close modal after updating notification
  };

  // Handler to mark a notification as read
  const handleMarkAsRead = (id) => {
    dispatch(markNotificationAsRead(id));
  };

  // Handler to delete a notification
  const handleDeleteNotification = (id) => {
    dispatch(deleteNotification(id));
  };

  // Open modal for creating a new notification
  const openCreateModal = () => {
    setNotificationData({ message: "", id: null }); // Reset form data for new notification
    setShowModal(true);
  };

  // Open modal for editing an existing notification
  const openEditModal = (notification) => {
    setNotificationData({ message: notification.message, id: notification._id });
    setShowModal(true);
  };

  // Handle form submission
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (notificationData.id) {
      handleUpdateNotification(notificationData); // Update existing notification
    } else {
      handleCreateNotification(notificationData); // Create new notification
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error fetching notifications</div>;

  return (
    <div className="notification-container">
      <h2>Notifications</h2>

      {/* Displaying notifications */}
      {notifications.length === 0 ? (
        <p>No notifications available.</p>
      ) : (
        notifications.map((notification) => (
          <div key={notification._id} className="notification-item">
            <p>{notification.message}</p>
            <p><strong>{notification.read ? "Read" : "Unread"}</strong></p>
            <button onClick={() => handleMarkAsRead(notification._id)}>
              Mark as Read
            </button>
            <button onClick={() => handleDeleteNotification(notification._id)}>
              Delete
            </button>
            <button onClick={() => openEditModal(notification)}>
              Edit
            </button>
          </div>
        ))
      )}

      {/* Button to create a new notification */}
      <button onClick={openCreateModal} className="create-notification-button">
        Create Notification
      </button>

      {/* Modal for creating/updating notification */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{notificationData.id ? "Edit Notification" : "Create Notification"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleFormSubmit}>
            <Form.Group controlId="notificationMessage">
              <Form.Label>Message</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter notification message"
                value={notificationData.message}
                onChange={(e) => setNotificationData({ ...notificationData, message: e.target.value })}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              {notificationData.id ? "Update" : "Create"}
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Notification;
