import React, { useState } from 'react'
import Sidebar from './Sidebar'
import Status from './Status'
import UserList from './UserList'
import './AdminDashboard.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import PostList from './PostList'

const AdminDashboard = () => {
  const [showUserList, setShowUserList] = useState(false);
  const [showPostList, setShowPostList] = useState(false);
  const handleViewUsersClick = () => {
    setShowUserList(!showUserList);
  };
  const handlePostUsersClick = () => {
    setShowPostList(!showPostList);
  };
  return (
    <>
     <div className="container-fluid">
     <h1 className="text-center ad">Admin Dashboard</h1>
      <div className="row">
        {/* Sidebar */}
        <div className="col-3 col-md-2 bg-dark text-white vh-100 position-fixed">
        <Sidebar onViewUsersClick={handleViewUsersClick} onPostUsersClick={handlePostUsersClick} />
        </div>

        {/* Main content area with offset */}
        <div className="col-9 col-md-10 offset-md-2 ms-auto p-4">
          <Status />
          {showUserList && <UserList />}
          {showPostList && <PostList/> }
        </div>
      </div>
    </div>
    </>
  )
}

export default AdminDashboard
