import React from 'react'
import './Sidebar.css'
import logo from '../../img/alogo.png'
import post from "../../img/share.png";
import noti from "../../img/noti.png";
import logout from "../../img/logout1.png";
import user1 from "../../img/user1.png";
import dashboard from "../../img/dashboard1.png";
import { useNavigate } from 'react-router-dom'; // Import useNavigate
const Sidebar = ({ onViewUsersClick, onViewPostsClick }) => {
    const navigate = useNavigate();
    const handleLogout = () => {
        // Redirect to the authentication page on logout
        localStorage.clear();
        navigate('/auth');
    };

    return (
        <>
            <div className="sidebar text-white p-3" style={ { width: '17%' } }>
                <div className="text-center">
                    <img
                        src={ logo }
                        alt="ChatLink Logo"
                        style={ { width: '100px', height: '100px', borderRadius: '50%', marginBottom: '20px' } }
                    />
                </div>
                <div className="d-flex flex-column">
                    <button className="btn btn-outline-warning mb-2" onClick={ onViewUsersClick }>View Users</button>
                    <button className="btn btn-outline-warning  mb-2" onClick={ onViewPostsClick }>View Posts</button>
                    <button className="btn btn-outline-warning mb-2">Send Notification</button>
                    <button className="btn btn-outline-danger text-light mb-2" onClick={ handleLogout }>Logout</button>
                    <a href='/'><img src={dashboard} className='imglogo' alt="icon" />Dashboard</a>
                    <a href='/'><img src={user1} className='imglogo' alt="icon" />View Users</a>
                    <a href='/'><img src={post} className='imglogo' alt="icon" />View Posts</a>
                    <a href='/'><img src={noti} className='imglogo' alt="icon" />Send Notification</a>
                    <a href='/'><img src={logout} className='imglogo' alt="icon" />Logout</a>
                    {/* <Link to={'../chat'}><img src={ Comment } alt="Comments" /></Link> */}
                </div>

                </div>
        
        </>
    )
}

export default Sidebar
