import React from 'react';
import { Button } from 'react-bootstrap';

const UserAdmin = ({ user, onEdit, onDelete, onBlock }) => {
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;

    return (
        <div className="user-admin">
            <div className="user-info">
                <img
                    src={user.profilePicture ? `${PF}${user.profilePicture}` : `${PF}default.png`}
                    alt="Profile"
                    className="rounded-circle"
                    width="40"
                />
                <div>
                    <h5>{`${user.firstname} ${user.lastname}`}</h5>
                    <p>{user.username}</p>
                    <p>{user.country}</p>
                    <p>{new Date(user.createdAt).toLocaleString()}</p>
                </div>
            </div>
            <div className="user-actions">
                <Button variant="info" size="sm" className="me-2" onClick={onEdit}>
                    Edit
                </Button>
                <Button variant="danger" size="sm" className="me-2" onClick={onDelete}>
                    Delete
                </Button>
                <Button variant="warning" size="sm" onClick={onBlock}>
                    Block
                </Button>
            </div>
        </div>
    );
};

export default UserAdmin;
