import React, { useEffect, useState } from "react";
import { getTimelinePosts } from "../actions/postAction";
import Post from "../post/Post";
import { useSelector, useDispatch } from "react-redux";
import "./Posts.css";

const Posts = () => {
  const dispatch = useDispatch();
  const { user } = useSelector((state) => state.authReducer.authData);
  const { posts = [], loading } = useSelector((state) => state.postReducer);
  const [updatedPosts, setUpdatedPosts] = useState(posts); // State to hold updated posts

  useEffect(() => {
    if (user && user._id) {
      dispatch(getTimelinePosts(user._id));
    }
  }, [dispatch, user]);

  useEffect(() => {
    setUpdatedPosts(posts); // Sync updated posts with the posts from the Redux state
  }, [posts]);

  const handleLikeToggle = (postId, newLikedStatus) => {
    // Update the posts state with the new likes array
    setUpdatedPosts((prevPosts) =>
      prevPosts.map((post) =>
        post._id === postId
          ? {
              ...post,
              likes: newLikedStatus
                ? [...post.likes, user._id] // Add user ID if liked
                : post.likes.filter((id) => id !== user._id), // Remove user ID if unliked
            }
          : post
      )
    );
  };

  return (
    <div className="Posts">
      {loading ? (
        "Fetching posts...."
      ) : updatedPosts && updatedPosts.length > 0 ? (
        updatedPosts.map((post) => (
          <Post
            data={post}
            key={post._id}
            onLikeToggle={handleLikeToggle} // Pass handleLikeToggle as a prop to each Post component
          />
        ))
      ) : (
        <p>No Posts Available</p>
      )}
    </div>
  );
};

export default Posts;
