import React, { useEffect, useState } from 'react';
import { Card, Button, Row, Col, Container } from 'react-bootstrap';
import { getTimelinePosts } from '../api/PostRequest'; // Import the API function

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const userId = '6757eeeabacab91a5683266c'; // Replace with actual user ID

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await getTimelinePosts(userId);
        setPosts(response.data); // Set the posts data from API response
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, [userId]);

  return (
    <Container className="mt-4">
      <Row xs={1} md={2} lg={3} className="g-4"> {/* Responsive grid */}
        {posts.map(post => (
          <Col key={post._id}> {/* Use post._id as the unique key */}
            <Card className="h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={post.image || 'default-image.jpg'} // Fallback if no image is available
                alt="Post"
                className="rounded"
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <Card.Body>
                <Card.Title>{post.title}</Card.Title>
                <Card.Text>
                  <strong>Author:</strong> {post.author} <br />
                  <strong>Date:</strong> {new Date(post.createdAt).toLocaleDateString()}
                </Card.Text>
                <div className="d-flex justify-content-between">
                  <Button variant="info" size="sm">
                    View Post
                  </Button>
                  <Button variant="warning" size="sm">
                    Edit
                  </Button>
                  <Button variant="danger" size="sm">
                    Delete
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default PostList;
