import React from 'react';
import { Table, Button } from 'react-bootstrap';
import img1 from '../../img/img1.png'; // Example image for posts
import img2 from '../../img/img2.png'; // Example image for posts
import img3 from '../../img/img3.png'; // Example image for posts
import img4 from '../../img/img4.png'; // Example image for posts

const PostList = () => {
  // Sample post data
  const posts = [
    { id: 1, title: 'Post 1', author: 'Joy Saha', image: img1, date: '2024-12-01' },
    { id: 2, title: 'Post 2', author: 'Jane Smith', image: img2, date: '2024-12-02' },
    { id: 3, title: 'Post 3', author: 'Alice Johnson', image: img3, date: '2024-12-03' },
    { id: 4, title: 'Post 4', author: 'Johnson Charlse', image: img4, date: '2024-12-04' },
    // Add more post data here
  ];

  return (
    <>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Image</th>
            <th>Title</th>
            <th>Author</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {posts.map(post => (
            <tr key={post.id}>
              <td><img src={post.image} alt="Post" className="rounded" width="40" /></td>
              <td>{post.title}</td>
              <td>{post.author}</td>
              <td>{post.date}</td>
              <td>
                <Button variant="info" size="sm" className="me-2">View Post</Button>
                <Button variant="warning" size="sm" className="me-2">Edit</Button>
                <Button variant="danger" size="sm">Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </>
  );
};

export default PostList;
