import React, { useEffect, useState } from 'react';
import { Button, Modal, Form } from 'react-bootstrap';
import { getAllUser, updateUser, deleteUser, blockUser, createUser } from '../api/UserRequest'; // Import API functions
import UserAdmin from './UserAdmin'; // Import the UserAdmin component

const UserListAdmin = () => {
    const [users, setUsers] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({ firstname: '', lastname: '', username: '', country: '' });
    const [editingUserId, setEditingUserId] = useState(null);

    // Fetch all users on component mount
    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await getAllUser();
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    // Handle Create or Edit User
    const handleSaveUser = async () => {
        try {
            if (editingUserId) {
                // Edit User
                const response = await updateUser(editingUserId, formData);
                setUsers(users.map((user) => (user._id === editingUserId ? response.data.user : user)));
            } else {
                // Create User
                const response = await createUser(formData);
                setUsers([...users, response.data.user]);
            }
            setShowModal(false);
            setFormData({ firstname: '', lastname: '', username: '', country: '' });
            setEditingUserId(null);
        } catch (error) {
            console.error('Error saving user:', error);
        }
    };

    // Open Modal for Edit
    const handleEditUser = (user) => {
        setFormData({ firstname: user.firstname, lastname: user.lastname, username: user.username, country: user.country });
        setEditingUserId(user._id);
        setShowModal(true);
    };

    // Handle Delete User
    const handleDeleteUser = async (id) => {
        try {
            await deleteUser(id);
            setUsers(users.filter((user) => user._id !== id));
            alert('User deleted successfully');
        } catch (error) {
            console.error('Error deleting user:', error);
        }
    };

    // Handle Block User
    const handleBlockUser = async (id) => {
        try {
            await blockUser(id);
            alert('User blocked successfully');
        } catch (error) {
            console.error('Error blocking user:', error);
        }
    };

    // Open Modal for Create User
    const handleCreateUser = () => {
        setFormData({ firstname: '', lastname: '', username: '', country: '' });
        setEditingUserId(null);
        setShowModal(true);
    };

    return (
        <>
            <Button variant="primary" className="mb-3" onClick={handleCreateUser}>
                Create New User
            </Button>

            <div className="UserListAdmin">
                <h4>User List</h4>
                {users.map((user) => (
                    <UserAdmin
                        key={user._id}
                        user={user}
                        onEdit={() => handleEditUser(user)}
                        onDelete={() => handleDeleteUser(user._id)}
                        onBlock={() => handleBlockUser(user._id)}
                    />
                ))}
            </div>

            {/* Create/Edit Modal */}
            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>{editingUserId ? 'Edit User' : 'Create User'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.firstname}
                                onChange={(e) => setFormData({ ...formData, firstname: e.target.value })}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.lastname}
                                onChange={(e) => setFormData({ ...formData, lastname: e.target.value })}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Username</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.username}
                                onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Country</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.country}
                                onChange={(e) => setFormData({ ...formData, country: e.target.value })}
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleSaveUser}>
                        Save
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
};

export default UserListAdmin;
