import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, Button } from 'react-bootstrap';

const UserList = () => {
  const [users, setUsers] = useState([]);

  // Fetch users from the database
  const fetchUsers = async () => {
    try {
      const response = await axios.get('/api/users');
      setUsers(response.data); // Assuming API returns an array of users
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };
  // Add New User
  const addUser = async (newUserData) => {
    try {
      const response = await axios.post('/api/users', newUserData);
      setUsers([...users, response.data]); // Add the new user to the list
    } catch (error) {
      console.error('Error adding user:', error);
    }
  };
  
  // Edit User
  const editUser = async (id, updatedData) => {
    try {
      const response = await axios.put(`/api/users/${id}`, updatedData);
      const updatedUser = response.data.user;
      setUsers(users.map((user) => (user._id === id ? updatedUser : user)));
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };
  
  // Delete a user
  const deleteUser = async (id) => {
    try {
      await axios.delete(`/api/users/${id}`);
      setUsers(users.filter((user) => user._id !== id)); // Update state after deletion
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  useEffect(() => {
    fetchUsers(); // Fetch users on component mount
  }, []);

  return (
    <>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Full Name</th>
            <th>Username</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.firstname} {user.lastname}</td>
              <td>{user.username}</td>
              <td>
                <Button variant="info" size="sm" className="me-2">
                  View
                </Button>
                <Button variant="warning" size="sm" className="me-2">
                  Edit
                </Button>
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => deleteUser(user._id)}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </>
  );
};

export default UserList;
