const authReducer = (
    state = { authData: null, loading: false, error: false, updateLoading: false },
    action
  ) => {
    switch (action.type) {
      case "AUTH_START":
        return { ...state, loading: true, error: false };
      case "AUTH_SUCCESS":
        // Extract user and token from action.data
        const { user, token } = action.data;
  
        // Store user and token separately for easier access
        const profile = { ...user, token };
  
        console.log("Processed Profile Data:", profile);
  
        // Save the processed profile in localStorage
        localStorage.setItem("profile", JSON.stringify(profile));
  
        return { ...state, authData: profile, loading: false, error: false };
      case "AUTH_FAIL":
        return { ...state, loading: false, error: true };
      default:
        return state;
    }
  };
  
  export default authReducer;
