import './App.css';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useSelector } from "react-redux";
import Blur from './components/blur/Blur';
import Auth from './pages/auth/Auth';
import Home from './pages/home/Home';
import Admin from './pages/admin/Admin'; // Uncomment Admin component import
// import Profile from './pages/profile/Profile';

function App() {
  const user = useSelector((state) => state.authReducer.authData);

  return (
    <>
      <div className="main">
        <Blur />

        <Routes>
          {/* Redirect based on authentication and admin status */}
          <Route 
            path="/" 
            element={
              user ? (
                user.isAdmin ? <Navigate to="admin" /> : <Navigate to="home" />
              ) : (
                <Navigate to="auth" />
              )
            } 
          />
          <Route 
            path="/home" 
            element={user ? <Home /> : <Navigate to="../auth" />} 
          />
          <Route 
            path="/auth" 
            element={user ? <Navigate to="../home" /> : <Auth />} 
          />
          <Route 
            path="/admin" 
            element={user && user.isAdmin ? <Admin /> : <Navigate to="../auth" />} 
          />
        </Routes>
      </div>
    </>
  );
}

export default App;
