import React, { useEffect, useState } from 'react';
import { Line, Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, registerables } from 'chart.js';
import { getAllUser } from '../api/UserRequest';
import { getTimelinePosts } from '../api/PostRequest';

ChartJS.register(...registerables);

const Graph = () => {
  const [userData, setUserData] = useState([]);
  const [postData, setPostData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const userResponse = await getAllUser();
      const users = userResponse.data;

      const postPromises = users.map((user) => getTimelinePosts(user._id));
      const postResponses = await Promise.all(postPromises);
      const posts = postResponses.flatMap((response) => response.data);

      setUserData(users);
      setPostData(posts);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // Data for Line Chart (User Growth Over Time)
  const userGrowthData = {
    labels: userData.map((user) => new Date(user.createdAt).toLocaleDateString()),
    datasets: [
      {
        label: 'Users Over Time',
        data: userData.map((_, index) => index + 1),
        borderColor: '#2980b9',
        backgroundColor: 'rgba(41, 128, 185, 0.5)',
      },
    ],
  };

  // Data for Bar Chart (Posts Over Time)
  const postActivityData = {
    labels: postData.map((post) => new Date(post.createdAt).toLocaleDateString()),
    datasets: [
      {
        label: 'Posts Over Time',
        data: postData.map((_, index) => index + 1),
        backgroundColor: '#239b56',
      },
    ],
  };

  // Data for Pie Chart (Likes Distribution)
  const likesDistributionData = {
    labels: postData.map((post) => post.desc || 'No Description'),
    datasets: [
      {
        label: 'Likes Distribution',
        data: postData.map((post) => post.likes.length),
        backgroundColor: [
          '#f5c32c',
          '#fca61f',
          '#2980b9',
          '#239b56',
          '#c0392b',
        ],
      },
    ],
  };

  return (
    <div>
      <h2>Admin Dashboard - Graphs</h2>

      <div style={{ marginBottom: '30px' }}>
        <h3>User Growth Over Time</h3>
        <Line data={userGrowthData} />
      </div>

      <div style={{ marginBottom: '30px' }}>
        <h3>Post Activity</h3>
        <Bar data={postActivityData} />
      </div>

      <div>
        <h3>Likes Distribution</h3>
        <Pie data={likesDistributionData} />
      </div>
    </div>
  );
};

export default Graph;
